function workingDaysBetweenDates(e,t){var a=["2017-02-20","2017-05-29","2017-07-04","2017-09-04","2017-10-09","2017-11-23","2017-12-25"],r=parseDate(e),n=parseDate(t);if(n<r)return"Sorry, this calculator only works if the FROM date is earlier than the TO date";var s=864e5;r.setHours(0,0,0,1),n.setHours(23,59,59,999);var o=n-r,i=Math.ceil(o/s),c=Math.floor(i/7);i-=2*c;var u=r.getDay(),l=n.getDay();u-l>1&&(i-=2),0===u&&6!==l&&i--,6===l&&0!==u&&i--;for(var D in a)a[D]>=e&&a[D]<=t&&i--;return i}function parseDate(e){var t=e.match(/(\d+)/g);return new Date(t[0],t[1]-1,t[2])}$(document).ready(function(){$(".js-calculate").click(function(){var e=$(".js-from-date").val(),t=$(".js-to-date").val();$(".js-result").text(workingDaysBetweenDates(e,t))})});